import React from 'react';
import { useNavigation } from '@react-navigation/native';
import { 
    View, 
    Text, 
    TouchableOpacity, 
    TextInput,
    Platform,
    StyleSheet ,
    StatusBar,
    Alert,
    ImageBackground 
} from 'react-native';

export default function homePage() {

    const navigation = useNavigation();

    function navigateToLogin() {
        navigation.navigate("Login");
    }


    return (
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
 <ImageBackground
        source={require('../assets/hands.png')}
        style={styles.background}
      >
        <View style={styles.header}>
            <Text style={styles.text_header}>Helping Hand</Text>

            <View style={styles.button}>
            <Text style={styles.text_MidSize}>Select a Role</Text>

                <TouchableOpacity
                    onPress= {()=>navigateToLogin()}
                    style={[styles.signIn, {
                        backgroundColor: '#FFFFFF',
                        marginTop: 15
                    }]}
                >
                    <Text style={[styles.textSign, {
                        color: '#009387'
                    }]}>Volunteer</Text>
                </TouchableOpacity>

                <TouchableOpacity
                    //onPress={}
                    style={[styles.signIn, {
                        backgroundColor: '#FFFFFF',
                        marginTop: 15
                    }]}
                >
                    <Text style={[styles.textSign, {
                        color: '#009387'
                    }]}>Coordinator</Text>
                </TouchableOpacity>

                <TouchableOpacity
                //onPress={}
                >
                <Text style={{color: '#FFFFFF', marginTop:15}}>Creating an Event?</Text>
            </TouchableOpacity>
            </View>
            </View>

      </ImageBackground>
      </View>
    );
}


const styles = StyleSheet.create({
    background: {
        width: '100%',
        height: '100%',
        resizeMode: 'contain'

      },
    text_MidSize: {
        color: '#05375a',
        fontSize: 15,
        fontWeight: 'bold'
    },
    container: {
      flex: 1, 
      backgroundColor: '#009387',
      width: '25%'
    },
    header: {
        flex: 3,
        justifyContent: 'center',
        paddingHorizontal: 20,
        paddingBottom: 20

    },
    footer: {
        flex: 3,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30
    },
    text_header: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 50,
        textAlign: 'center',
        paddingBottom: '20',
        textWithShadow:{
            textShadowColor: 'rgba(0, 0, 0, 0.75)',
            textShadowOffset: {width: -1, height: 20},
            textShadowRadius: 100}
    },
    text_footer: {
        color: '#05375a',
        fontSize: 18
    },
    action: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 5
    },
    actionError: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#FF0000',
        paddingBottom: 5
    },
    textInput: {
        flex: 1,
        marginTop: Platform.OS === 'ios' ? 0 : -12,
        paddingLeft: 10,
        color: '#05375a',
    },
    errorMsg: {
        color: '#FF0000',
        fontSize: 14,
    },
    button: {
        justifyContent: 'flex-end',
        alignItems: 'center',
        marginTop: 50
    },
    signIn: {
        width: '100%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10
    },
    textSign: {
        fontSize: 18,
        fontWeight: 'bold'
    }
  });
