import React from "react";

import { useNavigation } from "@react-navigation/native";
import {
  View,
  Text,
  TouchableOpacity,
  TextInput,
  Platform,
  StyleSheet,
  StatusBar,
  Alert,
  ImageBackground,
  Button,
} from "react-native";

import FontAwesome from "react-native-vector-icons/FontAwesome";

export default function createTask() {
  const navigation = useNavigation();

  //data will hold all of the text unput data
  const [data, setData] = React.useState({
    name: "",
    details: "",
    volunteers: "",
    location: "",
  });

  function handlePress() {
    //api call to submit the data in the form

    //console log to verify inputs for debugging
    console.log(data.name, data.details, data.volunteers, data.location);
  }

  //function to navigate back to task list on cancel
  function navigateToTaskList() {
    navigation.navigate("TaskList");
  }

  const nameInputChange = (val) => {
    setData({
      ...data,
      name: val,
    });
  };
  const detailsInputChange = (val) => {
    setData({
      ...data,
      details: val,
    });
  };
  const volunteersInputChange = (val) => {
    setData({
      ...data,
      volunteers: val,
    });
  };

  const locationInputChange = (val) => {
    setData({
      ...data,
      location: val,
    });
  };

  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
    <View style={styles.header}>
    <Text style={styles.text_header}>Helping Hand</Text>

      <TextInput
        placeholder="Task Name"
        style={styles.textInput}
        autoCapitalize="none"
        onChangeText={(val) => nameInputChange(val)}
      />
      <TextInput
        placeholder="Tasl Details"
        style={styles.textInput}
        autoCapitalize="none"
        onChangeText={(val) => detailsInputChange(val)}
      />
      <TextInput
        placeholder="Number of Volunteers Needed"
        style={styles.textInput}
        autoCapitalize="none"
        onChangeText={(val) => volunteersInputChange(val)}
      />
      <TextInput
        placeholder="Location"
        style={styles.textInput}
        autoCapitalize="none"
        onChangeText={(val) => locationInputChange(val)}
      />

      <TouchableOpacity
        onPress={() => navigateToTaskList()}
        style={[
          styles.signIn,
          {
            backgroundColor: "#FFFFFF",
            marginTop: 15,
          },
        ]}
      >
        <Text
          style={[
            styles.textSign,
            {
              color: "#009387",
            },
          ]}
        >
          Cancel
        </Text>
      </TouchableOpacity>

      <TouchableOpacity
        onPress={() => handlePress()}
        style={[
          styles.signIn,
          {
            backgroundColor: "#FFFFFF",
            marginTop: 15,
          },
        ]}
      >
        <Text
          style={[
            styles.textSign,
            {
              color: "#009387",
            },
          ]}
        >
          Submit
        </Text>
      </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#009387",
  },
  header: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 5,
    paddingBottom: 10,
    width: '100%',
    height: '100%',
    resizeMode: 'contain'
  },
  header2: {
    flex: 1,
    justifyContent: "flex-end",
    paddingHorizontal: 20,
    paddingBottom: 30,
  },
  footer: {
    flex: Platform.OS === "ios" ? 3 : 5,
    backgroundColor: "#fff",
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
    paddingHorizontal: 20,
    paddingVertical: 30,
  },
  text_header: {
    color: "#fff",
    fontWeight: "bold",
    fontSize: 30,
  },
  text_footer: {
    color: "#05375a",
    fontSize: 18,
  },
  action: {
    flexDirection: "column",
    marginTop: 10,
    borderBottomWidth: 1,
    borderBottomColor: "#f2f2f2",
    paddingBottom: 5,
  },
  textInput: {
    marginTop: Platform.OS === "ios" ? 0 : 0,
    paddingLeft: 10,
    color: "#05375a",
    borderWidth: 1,
    borderRadius: 10,
    height: 35,
    marginBottom: 20,
  },
  button: {
    alignItems: "center",
    marginTop: 50,
  },
  signIn: {
    width: "100%",
    height: 50,
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 10,
  },
  textSign: {
    fontSize: 18,
    fontWeight: "bold",
  },
  textPrivate: {
    flexDirection: "row",
    flexWrap: "wrap",
    marginTop: 20,
  },
  color_textPrivate: {
    color: "grey",
  },
});
