          <Text style={{marginBottom: 10}}>
            {props.description}
          </Text>
          <Button
            icon={<Icon name='check' color='#ffffff' />}
            onPress={() => closeTask()}
            buttonStyle={{borderRadius: 10, marginLeft: 0, marginRight: 0, marginBottom: 0,  backgroundColor: "#013d38"}}
            title='Close Task' />
      </Card>


    );
}
