import React from "react";

import { useNavigation } from "@react-navigation/native";
import { FloatingAction } from "react-native-floating-action";
import CoordCard from './coordCard';


import {
  View,
  Text,
  TouchableOpacity,
  TextInput,
  Platform,
  StyleSheet,
  Button,
} from "react-native";

import { Card, ListItem, Icon } from 'react-native-elements';



export default function coortinatorTask() {
    const navigation = useNavigation();

    const actions = [
        {
          text: "CreateTask",
          name: "bt_createTask",
          position: 1
        }]

  //function to navigate back to task list on cancel
  function navigateToCreateTask() {
    navigation.navigate("CreateTask");
  }
  const areasTest = [
    {
      id: "1",
      name: "Feed the Homeless",
      location: "Downtown Orlando",
      miles:"2.5 miles",
      description: "This is a description of feed the homeless. Need 8 participants to help go around DT Orlando to feed.",
      numVol: "5",
      maxVol: "8"
    },
    {
      id: "2",
      name: "Concert Cleanup",
      description: "This is a description of concert cleanup. Need 10 participants to help clean after a concert.",
      location: "Amway Center",
      miles:"3 miles",
      numVol: "1",
      maxVol: "10"
    },
    {
      id: "3",
      name: "Set up Tents",
      description: "This is a description of setting up tents. Need 6 participants.",
      location: "UCF",
      miles:"15 miles",
      numVol: "2",
      maxVol: "6"
    }
  ];

  const initialArr1 = [{
    color: "blue",
    text: "text1"
}, {
    color: "red",
    text: "text2"
}];

function renderButtons() {
        return (


            areasTest.map(areas=> (
              <CoordCard key={areas.name}
              name={areas.name}
              />

            ))
        ); 
}


  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center", backgroundColor: "#009387" }}>
    <View style={styles.header}>
    <Text style={styles.text_header}>New Task</Text>


      </View>

      <View style={styles.header}>
{
    renderButtons()
}


      </View>
      <FloatingAction
    actions={actions}
    onPressItem={()=>navigateToCreateTask()}
  />
    </View>
  );
}

const styles = StyleSheet.create({
    CoordCard: {
        alignSelf: "center",
        width: 10,
        height: 10
      },
  container: {
    flex: 1,
    backgroundColor: "#009387",
  },
  header: {
    flex: 1,
    justifyContent: "center",
    paddingHorizontal: 5,
    paddingBottom: 10,
    width: '90%',
    height: '90%',
    resizeMode: 'contain'
  },
  header2: {
    flex: 1,
    justifyContent: "flex-end",
    paddingHorizontal: 20,
    paddingBottom: 30,
  },
  footer: {
    flex: Platform.OS === "ios" ? 3 : 5,
    backgroundColor: "#fff",
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
    paddingHorizontal: 20,
    paddingVertical: 30,
  },
  text_header: {
    color: "#fff",
    fontWeight: "bold",
    fontSize: 30,
    alignSelf: "center",
    marginBottom: 30
  },
  text_footer: {
    color: "#05375a",
    fontSize: 18,
  },
  action: {
    flexDirection: "column",
    marginTop: 10,
    borderBottomWidth: 1,
    borderBottomColor: "#f2f2f2",
    paddingBottom: 5,
  },
  textInput: {
    marginTop: Platform.OS === "ios" ? 0 : 0,
    paddingLeft: 10,
    backgroundColor: "#0bbfb1",
    borderWidth: 1,
    borderRadius: 10,
    borderColor: "#009387",
    color: 'white',
    height: 35,
    marginBottom: 20,
  },
  button: {
    alignItems: "center",
    marginTop: 50,
  },
  signIn: {
    width: "100%",
    height: 50,
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 10,
  },
  textSign: {
    fontSize: 18,
    fontWeight: "bold",
  },
  textPrivate: {
    flexDirection: "row",
    flexWrap: "wrap",
    marginTop: 20,
  },
  color_textPrivate: {
    color: "grey",
  },
});
